import React from 'react';
import ConvertToFile from './ConvertToFile';

const Codewriter = ({content}) => {

    let Assembly_code = [];
    const mappingObj = { local : "LCL",argument : "ARG", this: "THIS",that: "THAT",temp : "5"};


    const pushHandler = (singleLine) => {
        const singleLine_content = singleLine.replace("push","");
        if(singleLine_content.includes("constant")){
            const temp = singleLine_content.replace("constant","");
            const singleLine_constant = temp.trim();
            const tobePushed = "@" + singleLine_constant;
            let comment = "// push" + singleLine_content;

            const constant_arr = [comment,tobePushed,"D=A","@SP","A=M","M=D","@SP","M=M+1"];
            Assembly_code = [...Assembly_code,...constant_arr];
            return Assembly_code;
        }
        else if(singleLine_content.includes("pointer")){
            const temp = singleLine_content.replace("pointer","");
            const singleLine_pointer = temp.trim();
            let tobePushed;
            if(singleLine_pointer === "0"){
                tobePushed  = "@THIS";
                console.log("This is the This keyword");
            }
            else if(singleLine_pointer === "1"){
                tobePushed  = "@THAT";
                console.log("This is the That keyword");

            }
            let comment = "// push" + singleLine_content;

            const constant_arr = [comment,tobePushed,"D=M","@SP","A=M","M=D","@SP","M=M+1"];
            Assembly_code = [...Assembly_code,...constant_arr];
            return Assembly_code;
        }
        else if(singleLine_content.includes("static")){
            let tobeSplitted = singleLine_content.trim();
            let splitted_static =  tobeSplitted.split(" ");
            const comment = "//push" + singleLine_content;
            let static_variable = "@vm." + splitted_static[1];
            const static_arr = [comment,static_variable,"D=M","@SP","A=M","M=D","@SP","M=M+1"];
            Assembly_code = [...Assembly_code,...static_arr]
            return Assembly_code
        }
        else{
            const tobeSeparated = singleLine_content.trim();
            const segments = tobeSeparated.split(" ");
            const firstValue = "@" +segments[1];
            let nextValue = "";
            let tempidentified = "";
            if(Object.keys(mappingObj).includes(segments[0])){
                    nextValue = "@" + mappingObj[segments[0]]
                    if(segments[0] === "temp"){
                        tempidentified = "A=A+D"
                    }
                    else{
                        tempidentified = "A=M+D"

                    }
            }
            let comment = "// push" + singleLine_content;
            const segmentArr = [comment,firstValue,"D=A",nextValue,tempidentified,"D=M","@SP","A=M","M=D","@SP","M=M+1"]
            Assembly_code = [...Assembly_code,...segmentArr]
            return Assembly_code 
        }
    }


    const popHandler = (singleLine) => {
        const singleLine_content = singleLine.replace("pop","");
        if(singleLine_content.includes("pointer")){
            const temp = singleLine_content.replace("pointer","")
            const singleLine_pointer = temp.trim();
            let tobeAdded;
            if(singleLine_pointer === "0"){
                tobeAdded  = "@THIS";
                console.log("This is the This keyword");
            }
            else if(singleLine_pointer === "1"){
                tobeAdded  = "@THAT";
                console.log("This is the That keyword");

            }

            let comment = "// pop" + singleLine_content;
            const pointerArr = [comment,"@SP","M=M-1","@SP","A=M","D=M",tobeAdded,"M=D"]
            Assembly_code = [...Assembly_code,...pointerArr]
            return Assembly_code;
        }
        else if(singleLine_content.includes("static")){
            let tobeSplitted = singleLine_content.trim();
            let splitted_static =  tobeSplitted.split(" ");

            const comment = "//pop" + singleLine_content;
            let static_variable = "@vm." + splitted_static[1];
            const static_arr = [comment,"@SP","M=M-1","@SP","A=M","D=M",static_variable,"M=D"];
            Assembly_code = [...Assembly_code,...static_arr]
            return Assembly_code
        }
        else{
            const tobeSeparated_Pop = singleLine_content.trim();
            const segments_pop = tobeSeparated_Pop.split(" ");
            const first_pop = "@" +segments_pop[1];
            let next_pop = "";
            let tempidentified = "";
            let comment = "// pop" + singleLine_content
            if(Object.keys(mappingObj).includes(segments_pop[0])){
                next_pop = "@" + mappingObj[segments_pop[0]]
                if(segments_pop[0] === "temp"){
                    tempidentified = "D=D+A"
                }
                else{
                    tempidentified = "D=D+M"

                }
            }
            const segment_pop_arr = [comment,first_pop,"D=A",next_pop,tempidentified,"@R13","M=D","@SP","M=M-1","@SP","A=M","D=M","@R13","A=M","M=D"];
            Assembly_code = [...Assembly_code,...segment_pop_arr]
            return Assembly_code
        }
    }

    const gotoHandler = (singleLine) => {
        const comment = "//" +singleLine;
        const singleLine_content = singleLine.replace("goto","");
        const goto_arr = [comment,"@" + singleLine_content.trim(),"0;JMP"];
        Assembly_code = [...Assembly_code,...goto_arr]
        return Assembly_code
    }

    const if_gotoHandler = (singleLine) => {
        const comment = "//" + singleLine;
        const singleLine_content= singleLine.replace("if-goto","");
        const label = "@" + singleLine_content.trim();
        const if_goto_arr = [comment,"@SP","M=M-1","@SP","A=M","D=M",label,"D;JNE"]
        Assembly_code = [...Assembly_code,...if_goto_arr]
        return Assembly_code
    }

    const labelHandler = (singleLine) => {
        const comment = "//" +singleLine;
        const singleLine_content = singleLine.replace("label","");
        const label_arr = [comment,"(" + singleLine_content.trim() + ")"];
        Assembly_code = [...Assembly_code,...label_arr]
        return Assembly_code
    }


    const artihmeticHandler = (singleLine_content) => {
        let mainSegment = "";
        let athArr;
        if(singleLine_content === "neg" || singleLine_content === "not"){
            let comment = "//" + singleLine_content
            if(singleLine_content === "neg"){
                athArr = [comment,"@SP","M=M-1","@SP","A=M","M=-M","@SP","M=M+1"];
            }
            else if(singleLine_content === "not"){
                athArr = [comment,"@SP","M=M-1","@SP","A=M","M=!M","@SP","M=M+1"];
            }

        }
        else if(singleLine_content === "gt"){
            let num = Math.floor(Math.random() *10);
            let gt_var = "@CHECKGT." + num;
            let gt_label = "(CHECKGT."+num+")";
            let prev_var = "@PREVIEWGT." + num;
            let prev_label = "(PREVIEWGT."+num+")";
            let comment = "//" + singleLine_content
            athArr = [comment,"@SP","M=M-1","@SP","A=M","D=M","@SP","M=M-1","@SP","A=M",
            "D=M-D",gt_var,"D;JGT","D=0",prev_var,"0;JMP",gt_label,"D=-1",prev_label,"@SP","A=M","M=D","@SP","M=M+1"];
        }
        else if(singleLine_content === "lt"){
            let num = Math.floor(Math.random() *10);
            let lt_var = "@CHECKLT." + num;
            let lt_label = "(CHECKLT."+num +")";
            let prev_var = "@PREVIEWLT." + num;
            let prev_label = "(PREVIEWLT."+num+")";
            let comment = "//" + singleLine_content
            athArr = [comment,"@SP","M=M-1","@SP","A=M","D=M","@SP","M=M-1","@SP","A=M",
            "D=M-D",lt_var,"D;JLT","D=0",prev_var,"0;JMP",lt_label,"D=-1",prev_label,"@SP","A=M","M=D","@SP","M=M+1"];
        }
        else if(singleLine_content === "eq"){
            let num = Math.floor(Math.random() *10);
            let eq_var = "@CHECKEQ." + num;
            let eq_label = "(CHECKEQ."+num+")";
            let prev_var = "@PREVIEWEQ." + num;
            let prev_label = "(PREVIEWEQ."+num+")";
            let comment = "//" + singleLine_content
            athArr = [comment,"@SP","M=M-1","@SP","A=M","D=M","@SP","M=M-1","@SP","A=M",
            "D=M-D",eq_var,"D;JEQ","D=0",prev_var,"0;JMP",eq_label,"D=-1",prev_label,"@SP","A=M","M=D","@SP","M=M+1"];
        }
        else{
            if(singleLine_content === "add"){
                mainSegment = "D=M+D"
            }
            else if(singleLine_content === "sub"){     
                mainSegment = "D=M-D";
            }
            else if(singleLine_content === "and"){
                mainSegment = "D=M&D"
            }
            else if(singleLine_content === "or"){
                mainSegment = "D=M|D"
            }
            let comment = "//" + singleLine_content
             athArr = [comment,"@SP","M=M-1","@SP","A=M","D=M","@SP","M=M-1","@SP","A=M",
            mainSegment,"@SP","A=M","M=D","@SP","M=M+1"];

        }

        Assembly_code = [...Assembly_code,...athArr]



        return Assembly_code;
    }


    const codeReplacer = (singleLine_content) => {
        if(singleLine_content.startsWith("push")){
            pushHandler(singleLine_content);
        }
        else if(singleLine_content.startsWith("pop")){
            popHandler(singleLine_content);
        }
        else if(singleLine_content.startsWith("goto")){
            gotoHandler(singleLine_content);
        }
        else if(singleLine_content.startsWith("label")){
            labelHandler(singleLine_content);
        }
        else if(singleLine_content.startsWith("if-goto")){
            if_gotoHandler(singleLine_content);
        }
        else if(singleLine_content.startsWith("call")){
            callHandler(singleLine_content);
        }
        else if(singleLine_content.startsWith("function")){
            functionHandler(singleLine_content);
        }
        else if(singleLine_content.startsWith("return")){

        }
        else {
            artihmeticHandler(singleLine_content);
        }
    }


        content.map((item) =>{
                return codeReplacer(item)
            }
        )



    return (
        <div>
            <ConvertToFile content={Assembly_code} />
        </div>
    );
}

export default Codewriter;
