import React, { useEffect } from 'react';
import './App.css';
import {useState} from 'react';
import AfterLoad from './Components/AfterLoad';

const App = () => {

    let fileReader;
    const [ content, setContent] = useState([]);
    const [ loaded,  setLoaded] = useState(false);

    const divider = (item,len) => {
        const regexFiles = /[A-Za-z0-9].vm/
         for(var x=0;x<len;x++){
            if(regexFiles.test(item[x].name)){
                    handleFileChosen(item[x],len);
                }
            }
            }

    const handleFileRead = (e) => {
            setLoaded(true)
            setContent((prevState) => {
                  return  [...prevState,...e.target.result.trim().split("\n")];
            })
        }

    const handleFileChosen = (file,len) => {
            fileReader = new FileReader();
            fileReader.onload = handleFileRead;
            fileReader.readAsText(file);
    }

    return (
        <div>
        <input 
                 type="file"
                 directory=""
                 webkitdirectory=""
                 onChange={ (e) => divider(e.target.files,e.target.files.length)}/>

        {loaded && <AfterLoad content={content}/>}

        </div>
    );
}
export default App;
