import React from 'react';
import './App.css';


const ImportFromFileCompoenent = () => {
    let fileReader;

    const handleFileRead = (e) => {
            const content = fileReader.result;
            console.log(content);
    }

    const handleFileChosen = (file) => {
            fileReader = new FileReader();
            fileReader.onloadend = handleFileRead;
            fileReader.readAsText(file);
    }
    return (
        <div >
            <input type='file'
                    accept=".asm"
                    onChange={ (e) => {
                        console.log("This is the Files",e.target.files);
                        handleFileChosen(e.target.files[0])}}/>
        </div>
    );
}

const Ainstruction = () => {

    return "Handling A instruction"
}


const App = () => {

    return (
        <div>
        <p>Hello there you can load your assembly code</p>
        <ImportFromFileCompoenent/>
        </div>
    );


}


export default App;
