import React from 'react';
import './index.css';

const Buttons = ({ truefalseFilter,letsStyle,disability,correctOne,previewCorrect,previewIncorrect,randomArray,handleAllAnswer}) => {

    const styleForCorrectAnswer =  {
        backgroundColor:'black',
        color:'white'
    }

    const onChoiceSelected = strIndex => {
        return () => (!previewCorrect && !previewIncorrect) ? handleAllAnswer(randomArray,strIndex) : null
    }
    let disabilityChecker = (strIndex) => (!letsStyle[strIndex] && (disability[strIndex] && !correctOne[strIndex]));
    let styleChecker =(strIndex) => letsStyle[strIndex] ? styleForCorrectAnswer : null;
    let trueFalseChecker = (strIndex) => !truefalseFilter[strIndex];



    return (
        <div>

        {trueFalseChecker("first")&&
        <button id="but1" className="but" type="button" 
        style={styleChecker("first")} 
        disabled= {disabilityChecker("first")} 
        onClick={onChoiceSelected("first")}>{randomArray[0]}</button>}

        {trueFalseChecker("second") &&
        <button id="but2" className="but" type="button" 
        style={styleChecker("second")} 
        disabled= {disabilityChecker("second")} 
        onClick={onChoiceSelected("second")}>{randomArray[1]}</button>}

        {trueFalseChecker("third") && 
        <button id="but3" className="but" type="button" 
        style={styleChecker("third")} 
        disabled= {disabilityChecker("third")} 
        onClick={onChoiceSelected("third")}>{randomArray[2]}</button>}

        {trueFalseChecker("fourth") && 
        <button id="but4" className="but" type="button" 
        style={styleChecker("fourth")} 
        disabled= {disabilityChecker("fourth")} 
        onClick={onChoiceSelected("fourth")}>{randomArray[3]}</button>}

        </div>
    );
}

export default Buttons;
