import React from 'react';


const ImportFromFileComponent = ({handleFileChosen}) => {

        const divider = (item,len) => {
            // const regexFiles = /[a-z].vm/
        for(var x=0;x<len;x++){
             if(item[x].includes(".vm")){
                handleFileChosen(item[x]);
                console.log("Yes baby",item[x])
                  }
                }
    }
    return (
        <div >
        <p>Hello there you can load your VM code</p>
        {/* <input type='file'
                accept=".vm"
                onChange={ (e) => handleFileChosen(e.target.files[0])}/> */}
                 <input 
                 type="file"
                 accept=".vm" 
                 directory=""
                 webkitdirectory=""
                 onChange={ (e) => divider(e.target.files,e.target.files.length)}/>
        </div>

    );
}

export default ImportFromFileComponent;
