import React from 'react';

const Codewriter = ({content}) => {

    const Assembly_code = [];

    const pushHandler = (singleLine) => {
        const singleLine_content = singleLine.trim();
        if(singleLine_content.includes("constant")){
            const temp = singleLine.replace("constant","");
            const singleLine_constant = temp.trim();
            const tobePushed = "@" + singleLine_constant;
            const constant_arr = [tobePushed,"D=A","@SP","A=M","M=D","@SP","M=M+1"]
            return console.log("This is the Number in the Constant",constant_arr);
        }
        else if(singleLine_content.includes("pointer")){
            const temp = singleLine.replace("pointer","");
            const singleLine_pointer = temp.trim();
            return console.log("This is the Number in the Pointer",singleLine_pointer);
        }
        else if(singleLine_content.includes("static")){
            const temp = singleLine.replace("static","");
            const singleLine_static = temp.trim();
            return console.log("This is the Number in the static",singleLine_static);
        }
        else{
            return console.log("This is the Push Rest of the Members",singleLine_content)
        }
    }
    const popHandler = (singleLine_content) => {
        if(singleLine_content.includes("constant")){
            return console.log("THis is the Pop Constant Handler",singleLine_content);
        }
        else if(singleLine_content.includes("pointer")){
            return console.log("This is the Pop Pointer Handler",singleLine_content);
        }
        else if(singleLine_content.includes("static")){
            return console.log("This is the Pop Static Handler",singleLine_content)
        }
        else{
            return console.log("This is the Pop Rest of the Members",singleLine_content)
        }
    }

    const artihmeticHandler = (singleLine_content) => {
        return console.log("This is the Arthimetic Members",singleLine_content);
    }

    const codeReplacer = (singleLine_content) => {
        if(singleLine_content.startsWith("push")){
            pushHandler(singleLine_content);
        }
        else if(singleLine_content.startsWith("pop")){
            popHandler(singleLine_content);
        }
        else{
            artihmeticHandler(singleLine_content);
        }
    }

    content.map((item) => 
        codeReplacer(item)
    );

    return (
        <div>
            Yo Niggas
        </div>
    );
}

export default Codewriter;
