const AfterLoad = ({content}) => {
    return (
        <div>
            <Commenthandling content={content}/>    
        </div>

    );
}

const Cinstruction = ({content}) => {

    const separator = (singleLine) => {
        let jmp,cmp,dst = "";
        if(singleLine.includes('=')){
            var dst_cmp = singleLine.split('=');
            dst = dst_cmp[0];
         }
        if(!dst_cmp[1].includes(";")){
             cmp = dst_cmp[1].trim();
        }
        else if(dst_cmp[1].includes(";")){
            if(!singleLine.includes('=')){
                cmp = dst_cmp[0];
            }
            else{
                var cmp_jmp = dst_cmp[1].split(";");
                cmp = cmp_jmp[0].trim();
                jmp = cmp_jmp[1].trim();  
            }

        }
        const separated = [cmp,dst,jmp]
        return separated;
    }

    const binaryConverter = (arrayOfSeparated) => {

            let compBinary = "";
            let destBinary = "000";
            let jumpBinary = "000";
            let cmp = arrayOfSeparated[0];
            let dst = arrayOfSeparated[1];
            let jmp = arrayOfSeparated[2];

            compare.map( (item) => {
                if(item.comparision === cmp){
                    compBinary =  item.toBinary;
                }
                return "compare";
            }) 

            if(dst !== undefined){
            Object.keys(destination).map((item) => {
                    if(item === dst){
                    destBinary = destination[item];
                    }
                    return "destination";
            })
            }

            if(jmp !== undefined){
                Object.keys(jump).map((item) => {
                    if(item === jmp){
                    jumpBinary = jump[item];
                    }
                    return "jump";
            })
            }
            const convertedBinaryCinst = [compBinary,destBinary,jumpBinary]
            return convertedBinaryCinst;
    }

    const concatinator = (convertedCinst) => {
        const convertedCinstruction = "111" + convertedCinst[0] + convertedCinst[1] + convertedCinst[2];
        return convertedCinstruction;
    }

    for(var x=0;x<content.length;x++){
        if(content[x].includes('=') || content[x].includes(';')){

            const arrayOfSeparated = separator(content[x]);
            const convertedCinst = binaryConverter(arrayOfSeparated);
            content[x] = concatinator(convertedCinst);
        }
    }
    console.log(content);
    return null;

}

const Ainstruction = ({content}) => {

    var regex = /@[0-9]/;

    const digits_count = (n) => {
        var count = 0;
        if(n >= 0) ++count;
        while (n/10 >= 1){
            n/= 10;
            ++count;
        }
        return count
    }


    const decimaltoBinary = (toBeBinary) => {
        const removedAt = toBeBinary.replace('@','');
        const converted = parseInt(removedAt,10).toString(2);
        const toBeFilled = 16 - digits_count(converted);
        const filler = '0'.repeat(toBeFilled);
        const binary = filler + converted;
        return binary;

    }


    for(var x=0;x<content.length;x++){
        if(regex.test(content[x])){
            const value = decimaltoBinary(content[x]);
            content[x] = value;
        }
    }
    return (
        <div>
            <Cinstruction content={content}/>
        </div>
    );
}


const App = () => {


    let fileReader;
    const [ content, setContent] = useState(" ");
    const [ loaded,  setLoaded] = useState(false);

    const handleFileRead = (e) => {
            setLoaded(true)
            setContent(() => fileReader.result.trim().split("\n"));
    }

    const handleFileChosen = (file) => {
            fileReader = new FileReader();
            fileReader.onloadend = handleFileRead;
            fileReader.readAsText(file);
    }

    return (
        <div>
        <ImportFromFileComponent handleFileChosen={handleFileChosen}/>
        {loaded && <AfterLoad content={content}/>}
        </div>
    );
}
export default App;
