                 type="file"
                 accept=".vm" 
                 directory=""
                 webkitdirectory=""
                 onChange={ (e) => e.target.files.map((item) => {
                        return handleFileChosen(item);
                 })}/>
        </div>

    );
}

export default ImportFromFileComponent;
