import React from 'react';
import './App.css';
import {useState} from 'react';

const destination = 
    {
     null  : "000",
     M : "001",
     D : "010" ,
     MD  : "011",
     A  : "100" ,
     AM : "101" ,
     AD : "110",
     AMD  : "111" 
    }
const jump = {
    null  : "000",
    JGT : "001",
    JEQ : "010" ,
    JGE  : "011",
    JLT  : "100" ,
    JNE : "101" ,
    JLE : "110",
    JMP  : "111" 
}

const compare = [
    { comparision: "0", toBinary: "0101010"},
    { comparision: "1", toBinary: "0111111"},
    { comparision: "-1", toBinary: "0111010"},
    { comparision: "D", toBinary: "0001100"},
    { comparision: "A", toBinary: "0110000"},
    { comparision: "M", toBinary: "1110000"},
    { comparision: "!D", toBinary: "0001101"},
    { comparision: "!A", toBinary: "0110001"},
    { comparision: "!M", toBinary: "1110001"},
    { comparision: "-D", toBinary: "0001111"},
    { comparision: "-A", toBinary: "0110011"},
    { comparision: "-M", toBinary: "1110011"},
    { comparision: "D+1", toBinary: "0011111"},
    { comparision: "A+1", toBinary: "0110111"},
    { comparision: "M+1", toBinary: "1110111"},
    { comparision: "D-1", toBinary: "0001110"},
    { comparision: "A-1", toBinary: "0110010"},
    { comparision: "D+A", toBinary: "0000010"},
    { comparision: "D-A", toBinary: "0010011"},
    { comparision: "A-D", toBinary: "0000111"},
    { comparision: "D&A", toBinary: "0000000"},
    { comparision: "D|A", toBinary: "0010101"},
    { comparision: "M-1", toBinary: "1110010"},
    { comparision: "D+M", toBinary: "1000010"},
    { comparision: "D-M", toBinary: "1010011"},
    { comparision: "M-D", toBinary: "1000111"},
    { comparision: "D&M", toBinary: "1000000"},
    { comparision: "D|M", toBinary: "1010101"}
]

const ImportFromFileComponent = ({handleFileChosen}) => {
    return (
        <div >
        <p>Hello there you can load your assembly code</p>
        <input type='file'
                accept=".asm"
                onChange={ (e) => handleFileChosen(e.target.files[0])}/>
        </div>

    );
}

const AfterLoad = ({content}) => {
    return (
        <div>
            <Cinstruction content={content}/>

        </div>

    );
}

const Cinstruction = ({content}) => {

    const concatinator = (compBinary,destBinary,jumpBinary) => {
        const convertedCinstruction = "111" + compBinary + destBinary + jumpBinary;
        console.log("THis is the COnverted Value",convertedCinstruction);
        return convertedCinstruction;
    }


    for(var x=0;x<content.length;x++){
        if(!content[x].includes('@')){
            console.log("This",content[x]);
            var splitted = content[x].split('=');
            console.log("THis is the splitted values", splitted[0], "and",splitted[1])
        }

        if(content[x].includes('=')){
            console.log("This is the List of the Contents",content[x]);
            let compBinary,destBinary,jumpBinary = "";
            let jmp,cmp,dst = "";
            var dst_cmp = content[x].split('=');
                 dst = dst_cmp[0];
                 if(dst === null){
                    destBinary = "000";
                 }
            if(!dst_cmp[1].includes(";")){

                 cmp = dst_cmp[1].trim();
                 if(jmp === undefined){
                    jumpBinary = "000";
                }
            }
            else if(dst_cmp[1].includes(";")){
                var cmp_jmp = dst_cmp[1].split(";");
                 cmp = cmp_jmp[0].trim();
                 jmp = cmp_jmp[1].trim();  
            }


            compare.map( (item) => {
                if(item.comparision === cmp){
                    compBinary =  item.toBinary;
                }
                return "compare";
            }) 

            if(dst !== undefined){
            Object.keys(destination).map((item) => {
                    if(item === dst){
                       destBinary = destination[item];
                    }
                    return "destination";
            })
            }

            if(jmp !== undefined){
                Object.keys(jump).map((item) => {
                    if(item === jmp){
                       jumpBinary = jump[item];
                    }
                    return "jump";
            })
            }
            const convertedCinst = concatinator(compBinary,destBinary,jumpBinary);
            content[x] = convertedCinst;
        }

    }
    return (
        <div>
            <Ainstruction content={content}/>
        </div>
    );

}

const Ainstruction = ({content}) => {

    var regex = /@[0-9]/;

    const digits_count = (n) => {
        var count = 0;
        if(n >= 0) ++count;
        while (n/10 >= 1){
            n/= 10;
            ++count;
        }
        return count
    }


    const decimaltoBinary = (toBeBinary) => {
        const removedAt = toBeBinary.replace('@','');
        const converted = parseInt(removedAt,10).toString(2);
        const toBeFilled = 16 - digits_count(converted);
        const filler = '0'.repeat(toBeFilled);
        const binary = filler + converted;
        return binary;

    }


    for(var x=0;x<content.length;x++){
        if(regex.test(content[x])){
            const value = decimaltoBinary(content[x]);
            content[x] = value;
        }
    }

    console.log("This is the Converted Full Version",content);

    return "A instruction"
}


const App = () => {


    let fileReader;
    const [ content, setContent] = useState(" ");
    const [ loaded,  setLoaded] = useState(false);

    const handleFileRead = (e) => {
            setLoaded(true)
            setContent(() => fileReader.result.trim().split("\n"));
    }

    const handleFileChosen = (file) => {
            fileReader = new FileReader();
            fileReader.onloadend = handleFileRead;
            fileReader.readAsText(file);
    }

    return (
        <div>
        <ImportFromFileComponent handleFileChosen={handleFileChosen}/>
        {loaded && <AfterLoad content={content}/>}
        </div>
    );


}

export default App;
