import React from 'react';
import './App.css';
import {useState} from 'react';


const ImportFromFileComponent = ({handleFileChosen}) => {

    return (
        <div >
        <p>Hello there you can load your assembly code</p>
        <input type='file'
                accept=".asm"
                onChange={ (e) => handleFileChosen(e.target.files[0])}/>
        </div>

    );
}

const AfterLoad = ({content}) => {
    return (
        <div>
            <Ainstruction content={content}/>
        </div>

    );
}

const Ainstruction = ({content}) => {

    var regex = /@[0-9]/;
    var test1 = "@167548";
    var test2 = "@2";
    var test3 = "@30";
    var test4 = "@SUM";
    var test5 = "@lelad";

    var result = regex.test(test1);
    var result2 = regex.test(test2);
    var result3 = regex.test(test3);
    var result4 = regex.test(test4);
    var result5 = regex.test(test5);
    console.log("This is the First Test",result);
    console.log("This is the Second Test",result2);
    console.log("This is the Third Test",result3);
    console.log("This is the Fourth Test", result4);
    console.log("This is the Fivth Test",result5);

    return "Handling A instruction"
}


const App = () => {


    let fileReader;
    const [ content, setContent] = useState(" ");
    const [ loaded,  setLoaded] = useState(false);

    const handleFileRead = (e) => {
            setLoaded(true)
            setContent(fileReader.result);
            console.log(content);
    }

    const handleFileChosen = (file) => {
            fileReader = new FileReader();
            fileReader.onloadend = handleFileRead;
            fileReader.readAsText(file);
    }

    return (
        <div>
        <ImportFromFileComponent handleFileChosen={handleFileChosen}/>
        {loaded && <AfterLoad/>}
        </div>
    );


}


export default App;
